import urllib.parse

@mcp.tool()
def generate_chart(
        chartConfig: dict,
        width: int = 500,
        height: int = 300,
        devicePixelRatio: int = 2,
        version: str = "4",
        encoding: str = "url",
) -> dict:
    """Return a signed QuickChart URL from a Chart.js config."""
    payload = {
        "c": json.dumps(chartConfig),
        "width": width,
        "height": height,
        "devicePixelRatio": devicePixelRatio,
        "version": version,
    }
    if encoding == "base64":
        payload = {
    "c": json.dumps(chartConfig),  # âœ… ensures false, not False
    "width": width,
    "height": height,
    "devicePixelRatio": devicePixelRatio,
    "version": version,
}
    url = f"{QC_URL}?{urllib.parse.urlencode(payload)}"
        
    return {"url": url}






