#| eval: false
@mcp.tool()
def download_chart(url: str, outputPath: str) -> str:
    """Download the chart image to `outputPath` and return local path."""
    bytes_ = httpx.get(url).content
    with open(outputPath, "wb") as f:
        f.write(bytes_)
    return outputPath
